import React from 'react';

const Address = (props) => {

    console.log(props.emails)

    function copy(e){
        let input = document.createElement('input');
        input.innerText = this.state.emails[e.currentTarget.parentNode.id];
        navigator.clipboard.writeText(props.emails[e.currentTarget.parentNode.id]);
    };

    function sendAddress() {
        let fullName = document.getElementById('fullName').value.toLowerCase().split(" ")

        let obj = { first: fullName.shift(),
                    last: fullName.join(""),
                    domain: document.getElementById('domain').value.toLowerCase() 
                                }
        console.log(obj)
        return props.single(obj);
    }

    let emails = props.emails.map((x, i) => {
        return <li id={i} key={i} onClick={(e)=>props.selectAddress(e.target.id)}>{x}
                    <button className="far fa-clipboard" onClick={copy}/>
              </li>
    })

    const position = props.selAdd ? {margin:'60px auto'}:{margin: '300px auto'}

        return (
            <div className="Address" style={position}>
                <span id="nameDomain">
                    <input id="fullName" type="text" placeholder="Elon Musk" />
                    <input id="domain" type="text" placeholder="tesla.com" />
                    <button id="submit" onClick={sendAddress}>Find email</button>
                </span>
                {emails}
            </div>

        )
}

export default Address;
