import React from 'react';


class App extends React.Component {
    constructor(props){
        super(props);

        this.state = {
            emails: [],
            CLIENT_ID: '605121789952-64i5u4ggvju6qhlmkrkv71ol78qbk5aa.apps.googleusercontent.com',
            API_KEY: 'AIzaSyBkrt40jx5wx3PG4S5W9B49Vm02RNh4vt0',
            DISCOVERY_DOCS: ["https://www.googleapis.com/discovery/v1/apis/gmail/v1/rest"],

            // Authorization scopes required by the API; multiple scopes can be
            // included, separated by spaces.
            SCOPES: 'https://www.googleapis.com/auth/gmail.readonly'

        }
        this.single = this.single.bind(this);
        this.copy = this.copy.bind(this);

        this.initClient = this.initClient.bind(this);
        this.updateSigninStatus = this.updateSigninStatus.bind(this);


    }


    initClient() {
        const s = this.state;
        let that = this;
        window.gapi.client.init({
          apiKey: s.API_KEY,
          clientId: s.CLIENT_ID,
          discoveryDocs: s.DISCOVERY_DOCS,
          scope: s.SCOPES
        }).then(function () {
          // Listen for sign-in state changes.
          window.gapi.auth2.getAuthInstance().isSignedIn.listen(that.updateSigninStatus);

          // Handle the initial sign-in state.
          that.updateSigninStatus(window.gapi.auth2.getAuthInstance().isSignedIn.get());

        }, function(error) {
            console.log(error);
            this.appendPre(JSON.stringify(error, null, 2));
        });
      }



    updateSigninStatus(isSignedIn) {
        var authorizeButton = document.getElementById('authorize_button');
        var signoutButton = document.getElementById('signout_button');
        if (isSignedIn) {
          authorizeButton.style.display = 'none';
          signoutButton.style.display = 'block';
          this.listLabels();
        } else {
          authorizeButton.style.display = 'block';
          signoutButton.style.display = 'none';
        }
      }


    /**
       *  Sign in the user upon button click.
       */
      handleAuthClick = (event) => {
        window.gapi.auth2.getAuthInstance().signIn();
        console.log(window.gapi)
      }

      /**
       *  Sign out the user upon button click.
       */
      handleSignoutClick = (event) => {
        window.gapi.auth2.getAuthInstance().signOut();
      }

      appendPre = (message) => {
        var pre = document.getElementById('content');
        var textContent = document.createTextNode(message + '\n');
        pre.appendChild(textContent);
      }


      listLabels = () => {
        let that = this;
        window.gapi.client.gmail.users.labels.list({
          'userId': 'me'
        }).then(function(response) {
          var labels = response.result.labels;
          that.appendPre('Labels:');

          if (labels && labels.length > 0) {
            for (let i = 0; i < labels.length; i++) {
              var label = labels[i];
              that.appendPre(label.name)
            }
          } else {
            that.appendPre('No Labels found.');
          }
        });
      }


    componentDidMount(){
        //const gmail = new gmailAPI(this.state.API_KEY, this.state.CLIENT_ID);
        window.gapi.load('client:auth2', this.initClient);
        // gapi = window.gapi

    }

    single() {
        console.log(window.gapi)
        fetch("http://localhost:4000/single", { 
            method: "POST",
            headers: {
                'Accept': 'application/json',   
                'Content-Type': 'application/json'
              },
            body: JSON.stringify({first: document.getElementById('firstName').value.toLowerCase(),
                                  last: document.getElementById('lastName').value.toLowerCase(),
                                  domain: document.getElementById('domain').value.toLowerCase() 
                                })
        }).then(data => {
            console.log(data);
            return data.json()})
        .then(emails => {
            this.setState( { emails } )
        })

    }

    copy(e) {
        let input = document.createElement('input');
        input.innerText = this.state.emails[e.currentTarget.parentNode.id]
        console.log(navigator)
        navigator.clipboard.writeText(this.state.emails[e.currentTarget.parentNode.id])
    }






    render(){

        let emails = this.state.emails.map((x, i) => {
            return <li id={i.toString()} key={i}>{x}<button className="far fa-clipboard" onClick={this.copy}></button></li>
        })
        return (
            <div className="App">
                <label>First Name<input id="firstName" type="text"></input></label>
                <label>Last Name<input id="lastName" type="text"></input></label>
                <label>Company's domain<input id="domain" type="text"></input></label>
                <button id="submit" onClick={this.single}>submit</button>

                <button id="authorize_button" onClick={this.handleAuthClick} style={{display: 'none'}}>Authorize</button>
                <button id="signout_button" onClick={this.handleSignoutClick} style={{display: 'none'}}>Sign Out</button>

                <div id="content"/>

                {/* <button id="upload" onclick={this.upload}>upload</button>
                <button id="test" onclick={this.test}>test</button> */}
                {emails}
            </div>
        )
    }
}

export default App;
