import React from 'react';


class App extends React.Component {
    constructor(props){
        super(props);

        this.state = {
            emails: []

        }
        this.single = this.single.bind(this);

    }

    single() {
        fetch("http://localhost:4000/single", { 
            method: "POST",
            headers: {
                'Accept': 'application/json',   
                'Content-Type': 'application/json'
              },
            body: JSON.stringify({first: document.getElementById('firstName').value.toLowerCase(),
                                  last: document.getElementById('lastName').value.toLowerCase(),
                                  domain: document.getElementById('domain').value.toLowerCase() 
                                })
        }).then(data => {
            console.log(data);
            return data.json()})
        .then(emails => {
            this.setState( { emails } )
        })

    }

    render(){

        let emails = this.state.emails.map((x, i) => {
            return <li key={i}>{x}</li>
        })
        return (
            <div className="App">
                <input id="firstName" type="text"></input>
                <input id="lastName" type="text"></input>
                <input id="domain" type="text"></input>
                <button id="submit" onClick={this.single}>submit</button>

                {/* <button id="upload" onclick={this.upload}>upload</button>
                <button id="test" onclick={this.test}>test</button> */}
                {emails}
            </div>
        )
    }
}

export default App;
