import React from 'react';
import axios from 'axios';
import Switch from "react-switch";

class Message extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            headers: {
                from:"vicfaucon@gmail.com",
                to:"vicfaucon@gmail.com",
                subject: "Full Stack Engineer position at "
            },
            text: '',
            templates: new Map(),
            rendered: '',
            template: '',
            mode: false,
            newTemplate: false
        }
    }

    async componentDidMount(){
        console.log(this.props);
        let response = await axios('http://localhost:4000/gettemplate');
        response = response.data.hits.hits;
        console.log(response);
        let templates = new Map();
        for(let key in response){
            templates.set(response[key]._id, response[key]._source);
        }
        this.setState( { templates }, ()=>{console.log(this.state)} );
        this.changeMode();
    }


    changeSubject = (e) => {
        let s = { ...this.state };
        s.subject = e.target.value;
        this.setState(s);
    }

    changeMessage = (e) => {
        let text = e.target.value;
        if(this.state.mode) {
            this.setState( { template: text, rendered: text } );
            if(this.props.name) this.setState( { text: this.loadTemplate() } );
        } else {
            this.setState( {rendered: text, text} )
        }
    }

    upFirst = (word) => {
        return word.charAt(0).toUpperCase() + word.slice(1);

    }

    changeMode = () => {
        if(!this.props.name || !this.state.templateId) return this.setState( {mode: true});

        this.setState( {mode: !this.state.mode }, async () => {
            if(!this.props.mode){
                this.templateAction('update');
                let response = await axios('http://localhost:4000/gettemplate');
                response = response.data.hits.hits;
                let templates = new Map();
                for(let key in response){
                    templates.set(response[key]._id, response[key]._source);
                }
                this.setState( { templates, rendered: this.state.text } );
            } else this.setState( {rendered: this.state.template} )
    });
    }



    placeAttribute = (e) => {

        if(e.target.value === 'Add attribute') return;


        let textemp = this.state.template.slice(0);

        let text = document.getElementById('emailEditor');

        let nwTemp = textemp.split("")

        if(text.selectionStart === text.selectionEnd){
            nwTemp.splice(text.selectionStart, 0, ..."{"+e.target.value+"}".split(''));
        } else {
            nwTemp.splice(text.selectionStart, text.selectionEnd - text.selectionStart, ..."{"+e.target.value+"}".split(''))
        }

        nwTemp = nwTemp.join("");

        console.log(nwTemp);

        this.setState( { template: nwTemp, rendered: nwTemp} );
        console.log(document.getElementsByTagName('select')[0]);
        document.getElementsByTagName('select')[0].value = 'Add attribute';

    }

    sendMessage = () => {
        let body = ""
        for(let h in this.state.headers){
            body += h += ": " + this.state.headers[h] + "\r\n"
        }
        body += "\r\n" + this.state.text;
        console.log(this.state.text);

        console.log(body)
        //replace(/\+/g, '-').replace(/\//g, '_')
        const sendRequest = window.gapi.client.gmail.users.messages.send({
            'userId': 'me',
            'resource': {
                'raw': window.btoa(body)
            }
        })

        return sendRequest.execute((a)=> {console.log(a)}, (error)=>{console.log(error)})

    }

    templateAction = async (action) => {
        switch(action){
            case "save":
                axios.post('http://localhost:4000/recordtemplate', {
                    name: document.getElementById("templateName").value,
                    subject: this.state.headers.subject,
                    text: this.state.template
                })
                break;

            case "delete":
                let del = await axios.post("http://localhost:4000/deletetemplate", {name: this.state.templateId});
                console.log(del);
                break;

            case "update":
                let upd = await axios.post('http://localhost:4000/updatetemplate', {name: this.state.templateId, text: this.state.template});
                console.log(upd);
                break;

            case "new":
                if(this.state.templateId) this.templateAction("update");
                this.setState({template:"", text:"", templateId: false, newTemplate: true})


        }

    }

    selectTemplate = (id) => {
        if(id === 'Add template') return;
        console.log(id)


        this.setState( {templateId: id}, () => {
            console.log(this.state.templateId);
        let text = '';

        if(this.state.mode) {
            text = this.state.templates.get(this.state.templateId);
            console.log(text);
            text = text.text;
            this.setState( {template:text, rendered:text} );
            if(this.props.name) this.setState( {text: this.loadTemplate()})
        }
        else {
            text = this.state.templates.get(this.state.templateId);
            text = text.text;
            this.setState( {template:text, text:this.loadTemplate(), rendered:this.loadTemplate()} )

        }
    });


    }

    loadTemplate = () => {
        let text = this.state.template.split('');
        console.log(text)

        let first = this.upFirst(this.props.name.first);
        let last = this.upFirst(this.props.name.last);
        let company = this.upFirst(this.props.name.domain.split('.')[0]);


        for(let i = 0; i < text.length-1; i++){
            if(text[i] === '{'){
                let j = i;
                while(text[j] !== "}"){
                    j = j + 1;
                }
                let word = text.slice(i+1, j).join('');
                let repwr;
                switch(word){
                    case "FullName":
                        repwr = first + " " + last;
                        break;
                    case "FirstName":
                        repwr = first;
                        console.log(text[i], first)
                        break;
                    case "LastName":
                        repwr = last;
                        break;
                    case "Company":
                        repwr = company;
                        break;
                }
                text.splice(i, j-i+1, repwr)

            }

        }

        return text.join('');

        // this.setState( {text, rendered: text} );

    }



    render(){

        let templates = [];

        this.state.templates.forEach((x, i) => {
            if(x) templates.push(<option key={i} value={x.name}>{x.name}</option>)
        })

        let tempSet = <span>
                        <select onClick={this.placeAttribute}>
                            <option value="Add attribute">Add attribute</option>
                            <option value="FullName">Full Name</option>
                            <option value="FirstName">First Name</option>
                            <option value="LastName">Last Name</option>
                            <option value="Company">Company</option>
                        </select>

                    </span>


        return(
            <div className="Message">

                <div className="EmailForm">
                    <input id="to" placeholder="To" value={this.props.selAdd}/>
                    <input  id="subject" placeholder="Subject" onChange={this.changeSubject}/>

                    <div id="setting">
                        {this.state.newTemplate ?  <input id="templateName" placeholder="Name the new template"/> : null}
                        {this.state.mode ? tempSet : null}

                        <Switch className="Switch" onChange={this.changeMode} checked={this.state.mode} />
                        {this.state.newTemplate ? null :
                            <select id="templatesName" onClick={(e) => this.selectTemplate(e.target.value)}>
                                <option>Add template</option>
                                {templates}
                            </select>}
                    </div>

                    <textarea  id="emailEditor" placeholder="Type your email" onChange={this.changeMessage} onClick={this.getCursPos} value={this.state.rendered}/>
                    {this.state.mode ? 
                    <span>
                        <button id="delete" onClick={(e)=>this.templateAction(e.target.id)}>Delete</button>
                        <button id="update" onClick={(e)=>this.templateAction(e.target.id)}>Update</button>
                    </span>
                    : <button onClick={this.sendMessage}>Send</button>}
                    {this.state.newTemplate ? <button id="save" onClick={(e)=>this.templateAction(e.target.id)}>Save</button> :
                    <button id="new" onClick={(e)=>this.templateAction(e.target.id)}>New</button>}
                </div>
            </div>
        )
    }
}

export default Message;
