import React from 'react';
import axios from 'axios';
import lodash from 'lodash';
import Switch from "react-switch";

class Message extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            headers: {
                from:"vicfaucon@gmail.com",
                to:"victorfaucon@hotmail.fr",
                subject: "Full Stack Engineer position at "
            },
            text: "",
            templates: [],
            template: false,
            mode: false
        }
    }

    async componentWillMount(){
        let templates = await axios('http://localhost:4000/gettemplate');
        templates = templates.data.hits.hits;
        this.setState( { templates }, ()=>{console.log(this.state)} );
    }


    changeSubject = (e) => {
        let s = { ...this.state };
        s.subject = e.target.value;
        this.setState(s);
    }

    changeMessage = (e) => {
        let text = e.target.value
        this.setState( { text } );
        if(this.state.mode) this.setState( { template: text } );
    }

    upFirst = (word) => {
        return word.charAt(0).toUpperCase() + word.slice(1);

    }

    changeMode = () => {
        if(!this.state.mode) this.setState( {text: this.state.template} );
        else this.loadTemplate();

        this.setState( {mode: !this.state.mode} )
    }



    placeAttribute = (e) => {

        if(e.target.value === 'Add attribute') return;


        let textemp = this.state.text.slice(0);

        let text = document.getElementById('emailEditor');

        let nwTemp = textemp.split("")

        if(text.selectionStart === text.selectionEnd){
            nwTemp.splice(text.selectionStart, 0, ..."{"+e.target.value+"}".split(''));
        } else {
            nwTemp.splice(text.selectionStart, text.selectionEnd - text.selectionStart, ..."{"+e.target.value+"}".split(''))
        }

        nwTemp = nwTemp.join("")

        console.log(nwTemp);

        this.setState( { template: nwTemp, text: nwTemp} );

    }

    sendMessage = () => {
        let body = ""
        for(let h in this.state.headers){
            body += h += ": " + this.state.headers[h] + "\r\n"
        }
        body += "\r\n" + this.state.text;

        console.log(body)

        const sendRequest = window.gapi.client.gmail.users.messages.send({
            'userId': 'me',
            'resource': {
                'raw': window.btoa(body).replace(/\+/g, '-').replace(/\//g, '_')
            }
        })

        return sendRequest.execute((a)=> {console.log(a)}, (error)=>{console.log(error)})

    }

    templateAction = async (e) => {
        switch(e.target.id){
            case "save":
                let resp = await axios.post('http://localhost:4000/recordtemplate', {
                    name: document.getElementById("templateName").value,
                    subject: this.state.headers.subject,
                    text: this.state.template
                });
                console.log(resp);
                break;

            case "delete":
                let del = await axios.post("http://localhost:4000/deletetemplate", {name: this.state.templateId});
                console.log(del);
                break;

            case "update":
                let upd = await axios.put('http://localhost:4000/updatetemplate', {})

        }

    }

    selectTemplate = (e) => {
        if(e.target.value === 'Add template') return;
        this.setState( {templateId: e.target.value}, this.loadTemplate)

    }

    loadTemplate = () => {
        let that = this;

        let template = this.state.template || lodash.find(that.state.templates, function(x){return x._source.name === that.state.templateId})._source.text;
        let text = template.split('');

        let first = this.upFirst(this.props.name.first);
        let last = this.upFirst(this.props.name.last);
        let company = this.upFirst(this.props.selAdd.split('@')[1].split('.')[0]);


        for(let i = 0; i < text.length-1; i++){
            if(text[i] === '{'){
                let j = i;
                while(text[j] !== "}"){
                    j = j + 1;
                }
                let word = text.slice(i+1, j).join('');
                let repwr;
                switch(word){
                    case "FullName":
                        repwr = first + " " + last;
                        break;
                    case "FirstName":
                        repwr = first;
                        console.log(text[i], first)
                        break;
                    case "LastName":
                        repwr = last;
                        break;
                    case "Company":
                        repwr = company;
                        break;
                }
                text.splice(i, j-i+1, repwr)

            }

        }

        text = text.join("");

        this.setState( {template, text})

    }



    render(){

        let templates = this.state.templates.map((x, i) => {
            return <option key={i} value={x._source.name}>{x._source.name}</option>
        })

        return(
            <div className="Message">
                <div className="EmailForm">
                    <input placeholder="To" value={this.props.selAdd}/>
                    <input  placeholder="Subject" onChange={this.changeSubject}/>
                    <select onClick={this.placeAttribute}>
                        <option>Add attribute</option>
                        <option value="FullName">Full Name</option>
                        <option value="FirstName">First Name</option>
                        <option value="LastName">Last Name</option>
                        <option value="Company">Company</option>
                    </select>
                        <Switch onChange={this.changeMode} checked={this.state.mode} />
                    <select onClick={this.selectTemplate}>
                        <option>Add template</option>
                        {templates}
                    </select>
                    <input id="templateName" placeholder="Name the new template"></input>
                    <textarea  id="emailEditor" placeholder="Type your email" onChange={this.changeMessage} onClick={this.getCursPos} value={this.state.text}/>
                    <button onClick={this.sendMessage}>Send</button>
                    <button id="save" onClick={this.saveTemplate}>Save Template</button>
                    <button id="delete" onClick={this.deleteTemplate}>Delete</button>
                    <button id="update" onClick={this.updateTemplate}>Update Template</button>
                </div>
            </div>
        )
    }
}

export default Message;
